import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const yearsSelect = [
    2003,
    2004,
    2005,
    2006,
    2007,
    2008,
    2009,
    2010,
    2011,
    2012,
    2013,
    2014,
    2015
  ];
  const [yearSelected, setYearSelected] = useState();
  const [yearRoundsData, setYearRoundsData] = useState([]);

  const [teams, setTeams] = useState([]);

  async function getYearData(year) {
    try {
      const response = await axios.get(`http://localhost:3001/${year}`);
      if (response.status === 200 && response.data) {
        setYearRoundsData(response.data);
      }
    } catch (error) {
      console.error(error);
    }
  }

  useEffect(() => {
    if (yearSelected) getYearData(yearSelected);
    else setYearRoundsData([]);
  }, [yearSelected]);

  useEffect(() => {
    if (yearRoundsData.length) {
      const arr = [];
      yearRoundsData.forEach(round => {
        if (!arr.includes(round.mandante)) arr.push(round.mandante);
      });
      console.log(arr);
    }
  }, [yearRoundsData]);

  return (
    <div className="App">
      <div className="header">
        <div className="title">Campeonato Brasileiro</div>
        <div className="select">
          <select onChange={event => setYearSelected(event.target.value)}>
            <option value="">Selecione o ano</option>
            {yearsSelect.map(year => {
              return (
                <option key={year} value={year}>
                  {year}
                </option>
              );
            })}
          </select>
        </div>
      </div>
    </div>
  );
};

export default App;
