import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import { nanoid } from "nanoid";

const App = () => {
  const yearsSelect = [
    2003,
    2004,
    2005,
    2006,
    2007,
    2008,
    2009,
    2010,
    2011,
    2012,
    2013,
    2014,
    2015
  ];
  const [yearSelected, setYearSelected] = useState();
  const [yearData, setYearData] = useState([]);

  const [teamsMatchesByYear, setTeamsMatchesByYear] = useState([]);

  async function getYearData(year) {
    try {
      const response = await axios.get(`http://localhost:3001/${year}`);
      if (response.status === 200 && response.data) {
        setYearData(response.data[response.data.length - 1].partidas);
      }
    } catch (error) {
      console.error(error);
    }
  }

  useEffect(() => {
    if (yearSelected) getYearData(yearSelected);
    else setYearData([]);
  }, [yearSelected]);

  useEffect(() => {
    if (yearData.length) {
      const arr = [];
      yearData.forEach(partida => {
        if (!arr.includes(partida.mandante)) {
          arr.push({
            name: partida.mandante,
            score: partida.pontuacao_geral_mandante
          });
        }
        if (!arr.includes(partida.visitante)) {
          arr.push({
            name: partida.visitante,
            score: partida.pontuacao_geral_visitante
          });
        }
      });

      setTeamsMatchesByYear(
        arr.sort((x, y) => y.score.total_pontos - x.score.total_pontos)
      );
    }
  }, [yearData]);

  const normalizeName = name => {
    if (!name) return name;
    return name
      .replace(/\s|_|\(|\)/g, "_")
      .normalize("NFD")
      .replace(/\p{Diacritic}/gu, "")
      .toLowerCase();
  };

  return (
    <div className="App">
      <div className="App-header">
        <div className="Title">
          <h1>Campeonato Brasileiro</h1>
        </div>
        <div className="Select">
          <select onChange={event => setYearSelected(event.target.value)}>
            <option value="">Selecione o ano</option>
            {yearsSelect.map(year => {
              return (
                <option key={year} value={year}>
                  {year}
                </option>
              );
            })}
          </select>
        </div>
      </div>
      <div className="Body">
        <h3>Classificação</h3>
        <table>
          <thead>
            <tr>
              <td>Time</td>
              <td>Pontos</td>
              <td>Vitórias</td>
              <td>Empates</td>
              <td>Derrotas</td>
              <td>Gols pró</td>
              <td>Gols contra</td>
              <td>Saldo de gols</td>
            </tr>
          </thead>
          <tbody>
            {teamsMatchesByYear.map(team => {
              return (
                <tr key={nanoid()}>
                  <td>
                    <img
                      src={`./img/${normalizeName(team.name)}`}
                      alt={team.name}
                    />
                    {team.name}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
