import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import { nanoid } from "nanoid";

const App = () => {
  const yearsSelect = [
    2003,
    2004,
    2005,
    2006,
    2007,
    2008,
    2009,
    2010,
    2011,
    2012,
    2013,
    2014,
    2015
  ];
  const [yearSelected, setYearSelected] = useState();
  const [yearData, setYearData] = useState([]);

  const [teams, setTeams] = useState([]);

  async function getYearData(year) {
    try {
      const response = await axios.get(`http://localhost:3001/${year}`);
      if (response.status === 200 && response.data) {
        setYearData(response.data);
      }
    } catch (error) {
      console.error(error);
    }
  }

  useEffect(() => {
    if (yearSelected) getYearData(yearSelected);
    else setYearData([]);
  }, [yearSelected]);

  useEffect(() => {
    if (yearData.length) {
      const arr = [];
      console.log(yearData);
      yearData.forEach(data => {
        data.partidas.forEach(partida => {
          if (!arr.includes(partida.mandante))
            arr.push({
              name: partida.mandante,
              score: partida.pontuacao_geral_mandante
            });
        });
      });
      setTeams(arr);
    }
  }, [yearData]);

  return (
    <div className="App">
      <div className="App-header">
        <div className="Title">
          <h1>Campeonato Brasileiro</h1>
        </div>
        <div className="Select">
          <select onChange={event => setYearSelected(event.target.value)}>
            <option value="">Selecione o ano</option>
            {yearsSelect.map(year => {
              return (
                <option key={year} value={year}>
                  {year}
                </option>
              );
            })}
          </select>
        </div>
      </div>
      <div className="Body">
        <h3>Classificação</h3>
        <table>
          <thead>
            <tr>
              <td>Time</td>
              <td>P</td>
              <td>V</td>
              <td>E</td>
              <td>D</td>
              <td>GP</td>
              <td>GC</td>
              <td>S</td>
            </tr>
          </thead>
          <tbody>
            {teams.map(team => {
              return (
                <tr key={nanoid()}>
                  <td>{team}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
